import React from "react";
import { FaSyncAlt, FaShieldAlt, FaTachometerAlt, FaUserCheck, FaClock, FaCogs } from "react-icons/fa";
import BenefitsCard from "./BenefitsCard";
import "./benefits.css";

const benefitsData = [
  {
    icon: <FaSyncAlt />,
    title: "Seamless Message Conversion",
    description:
      "FinFlow automates the conversion of financial messages across various formats (e.g., MT103, MT202, ISO 20022, etc.), ensuring no manual intervention and reducing the risk of errors.",
  },
  {
    icon: <FaShieldAlt />,
    title: "Improved Compliance",
    description:
      "The solution ensures compliance with international messaging standards like SWIFT's ISO 20022, facilitating smooth integration with global and regional payment systems.",
  },
  {
    icon: <FaTachometerAlt />,
    title: "Faster Processing Speed",
    description:
      "By automating the message conversion process, FinFlow significantly improves processing speed, leading to faster transaction times.",
  },
  {
    icon: <FaUserCheck />,
    title: "Reduced Manual Effort",
    description:
      "The automation of message conversion reduces the need for manual work, freeing up employee time for more strategic tasks and minimizing human errors.",
  },
  {
    icon: <FaClock />,
    title: "Real-Time Monitoring and Issue Resolution",
    description:
      "FinFlow offers real-time processing status with detailed summaries and error notifications, enabling quick resolution of any issues that arise.",
  },
  {
    icon: <FaCogs />,
    title: "Customization and Flexibility",
    description:
      "With features like dynamic mapping, rule-based routing, message grouping, and encryption, FinFlow provides a customizable and flexible solution that meets specific business needs while ensuring data security.",
  },
];

const Benefits = () => {
  return (
    <section className="benefits-container">
      <h2 className="page-title">The Big Benefits of FinFlow</h2>
      <div className="benefits grid">
        {benefitsData.map((benefit, index) => (
          <BenefitsCard key={index} icon={benefit.icon} title={benefit.title} description={benefit.description} />
        ))}
      </div>
    </section>
  );
};

export default Benefits;
