import React from "react";
import Heading from "../../common/heading/Heading";
import styles from "./Hero.css";

const Hero = () => {
  return (
    <>
      <section className="hero">
        <div className="container">
          <div className="row">
            <Heading
              subtitle="WELCOME TO FINFLOW"
              title="Eliminate chaos, enable seamless conversion"
            />
            <p
              style={{
                fontSize: "16px",
                color: "#555555",
                fontWeight: "normal",
              }}
            >
              Stop struggling with manual message format conversions between MX,
              MT, Fedwire, and ISO20022. Introducing FinFlow, your one-stop
              solution for automated, error-free message processing.
            </p>
            <div className="button">
            <section className={styles.hero}>
            <section className={styles.hero}>
  <button className={styles.primaryBtn}>GET STARTED NOW</button>
  <button className={styles.secondaryBtn}>EXPLORE FEATURES</button>
</section>

</section>
            </div>
          </div>
        </div>
      </section>
      <div className="margin"></div>
    </>
  );
};

export default Hero;
