import React from "react"
import Heading from "../../common/heading/Heading"
import "./Hero.css"

const Hero = () => {
  // Function to scroll to a specific section based on the provided id
  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop,
        behavior: "smooth",
      });
    }
  };

  return (
    <section className="hero">
      <div className="container">
        <div className="row">
          <h2>WELCOME TO FINFLOW</h2>
          <p>Eliminate chaos, enable seamless conversion</p>
          <p>
            Stop struggling with manual message format conversions between MX,
            MT, Fedwire, and ISO20022. Introducing FinFlow, your one-stop
            solution for automated, error-free message processing.
          </p>
          <div className="button-group">
            <button
              className="btn btn-primary"
              onClick={() => scrollToSection("about")}
            >
              GET STARTED NOW
              <i className="fa fa-long-arrow-alt-right"></i>
            </button>
            <button
              className="btn btn-secondary"
              onClick={() => scrollToSection("features")}
            >
              EXPLORE FEATURES
              <i className="fa fa-long-arrow-alt-right"></i>
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
